import{_ as a,o as t,c as e,Q as o}from"./chunks/framework.83854b5a.js";const m=JSON.parse('{"title":"مقدمة تعريفية","description":"","frontmatter":{"title":"مقدمة تعريفية","lang":"ar-MA"},"headers":[],"relativePath":"ar/guide/introduction.md","filePath":"ar/guide/introduction.md","lastUpdated":null}'),r={name:"ar/guide/introduction.md"},i=o('<h1 id="مقدمة-تعريفية" tabindex="-1">مقدمة تعريفية <a class="header-anchor" href="#مقدمة-تعريفية" aria-label="Permalink to &quot;مقدمة تعريفية&quot;">​</a></h1><p>البرنامج عبارة عن مجموعة انطمة للإدارة و التجارة ,توفر طريقة فعالة ومتكاملة لتتبع المخزون و العمليات التجارية كالمبيعات والمشتريات وما الى ذلك. دعنا نتعمق في الميزات الرئيسية ونوضح مدى سهولة استخدامه</p><h2 id="نظرة-عامة-على-لوحة-التحكم" tabindex="-1">نظرة عامة على لوحة التحكم <a class="header-anchor" href="#نظرة-عامة-على-لوحة-التحكم" aria-label="Permalink to &quot;نظرة عامة على لوحة التحكم&quot;">​</a></h2><p>تقدم لوحة التحكم واجهة مستخدم سهلة وفعالة للإدارة. سهولة البحث عن المعلومات تتبع لكل العمليات التجارية, دعم لعدة لغات , تقارير مفصلة وحسابات الية.</p><h2 id="ضبط-الإعدادات" tabindex="-1">ضبط الإعدادات <a class="header-anchor" href="#ضبط-الإعدادات" aria-label="Permalink to &quot;ضبط الإعدادات&quot;">​</a></h2><p>يُمكنك تحديد معلومات الشركة بسهولة، وتعيين إعدادات العملة واختيار العميل أو المستودع الافتراضي بشكل مرن. كما يُمكن تحديد أنماط معينة للفواتير، ويُتيح إمكانية إعداد نسخ احتياطية بالتخزين السحابي مع إمكانية إدارة النسخ الاحتياطية بفعالية، بما في ذلك تنظيفها بشكل محدد.</p><p>للحصول على دليل شامل لإعداد النظام، يرجى الرجوع إلى <a href="/docs/ar/guide/installation.html">دليل التثبيت</a>.</p>',7),n=[i];function d(c,s,l,_,h,p){return t(),e("div",null,n)}const f=a(r,[["render",d]]);export{m as __pageData,f as default};
